import "core-js/modules/es.array.push.js";
import { request } from '@cndinfo/cube-web-utils';
import '../../../lib/styles/components/AgSearchSelect.scss';
import localPagination from '../Pagination/index.js';
export default {
  name: 'AgSearchSelect',
  components: {
    localPagination
  },
  props: {
    // 后端接口页码是否 0 开始
    hasZeroPagination: {
      type: Boolean,
      default: true
    },
    // 接口地址
    defaultUrl: {
      type: String,
      default: ''
    },
    // 检索类型
    type: {
      type: String,
      default: 'staff'
    },
    // 客商类型字段
    customerType: {
      type: String,
      default: ''
    },
    // 货号类型字段
    artnoType: {
      type: String,
      default: ''
    },
    // 商品分类
    categories: {
      type: String,
      default: ''
    },
    // 检索字段
    searchKey: {
      type: String,
      default: 'sName'
    },
    value: {
      type: [String, Array],
      default: ''
    },
    multipleLimit: {
      type: Number,
      default: 0
    },
    filterMethod: {
      type: Function,
      default: null
    },
    disabled: {
      type: Boolean,
      default: false
    },
    multiple: {
      type: Boolean,
      default: false
    },
    clearable: {
      type: Boolean,
      default: true
    },
    placeholder: {
      type: String,
      default: ''
    },
    size: {
      type: String,
      default: 'mini'
    },
    option: {
      type: Object,
      default: () => {
        return {
          seledLabel: 'sName',
          label: 'sPath',
          value: ''
        };
      }
    },
    autoFocus: {
      type: Boolean,
      default: false
    },
    // 组件唯一标识
    mark: {
      type: String,
      default: ''
    },
    // 当前行数据（ag-grid中使用必须有）
    params: {
      type: Object,
      default: () => {
        return {};
      }
    },
    otherOptions: {
      type: Object,
      default: () => {}
    },
    transformSearchParams: {
      type: Function,
      default: searchParams => searchParams
    },
    cancelPage: {
      type: Boolean,
      default: false
    },
    method: {
      type: String,
      default: 'post'
    },
    // 是否默认人民币
    currency: {
      type: String,
      default: '人民币'
    },
    labelDefault: {
      type: String,
      default: ''
    }
  },
  data() {
    return {
      loadingText: '加载中',
      selectValue: this.value,
      defaultOption: {
        seledLabel: 'sName',
        label: 'sPath',
        value: ''
      },
      markId: '',
      remoteValue: '',
      pagination: {
        total: 0,
        pageNo: this.hasZeroPagination ? 0 : 1,
        pageSize: 20
      },
      loading: false,
      options: [],
      url: {
        company: '/org/dialog/corp/v2/page',
        depart: '/org/dialog/busi/v2/page',
        cost: '/org/dialog/cost/v2/page',
        area: '/area/dialog/v2/page',
        staff: '/staff/dialog/org/v2/page',
        customer: '/customer/dialog/v2/page',
        currency: '/currency/v2/page',
        artno: '/dialog/artno/v2/page',
        measurementunit: '/measurementunit/dialog/v2/query',
        warehouse: '/warehouse/info/dialog/v2/page-dialog',
        // creater: '/user/dialog/v2/page',
        creater: '/user/dialog/path/page',
        bank: '/bank/dialog/v2/page',
        tax: '/tax/type/page',
        goods: '/goods/dialog/detail/query/v2',
        applicant: '/user/dialog/path/page',
        // root: '/org/bdaorguserrelation/get/by/current/user',
        // portcity: '/portcity/dialog/name/like',
        portcustoms: '/portcustoms/dialog/name/like',
        groupcustomers: '/bda/groupCustomer/dialog/v2/page',
        bookingOrder: '/stock/booking/dialog/v2/page',
        // cargoContainer: '/comm/container/dialog/v2/page',
        company_depart: '/org/dialog/corporbus/page',
        staff_cost: '/staff/info/byorgid',
        sModifier: '/paper/homepage/notice/system/page',
        role: '/role/dialog/page',
        tenant: '/org/dialog/root/page',
        menu: '/sys/menu',
        bankCategory: '/bankCategory/page',
        hscode: '/hscode/bdahscode/dialog/page-dialog',
        root: '/org/root/page',
        paperCustomer: 'paper/customer/dialog/v2/page',
        pscmGoodsDetail: 'pscm/goods/detail/query/v2/page',
        pscmGoodsAttribute: 'pscm/goods/attribute/query/v2/page',
        paperStock: 'paper/stock/balance/summary/sum/query',
        cargoContainer: 'comm/container/dialog/v2/page',
        staffDialog: '/staff/dialog/org/v3/page',
        // portcity: '/portcity/dialog/name/like',
        orgRoot: '/org/root/page',
        portcity: '/portcity/page',
        interaction: '/interaction/payconfig/page',
        factor: '/sys/datatableresource/dialog/page',
        affectField: '/sys/datafieldresource/dialog/page',
        QC: '/configure/liorgrelation/role-user/page',
        transfer: '/configure/liorgrelation/role-user/page',
        customerLevel: '/bda/user/customer/level/dialog/page',
        taxType: '/tax/type/dialog/v2/page',
        taxationtype: '/invoice/sale/tax-detail/taxationtype/page',
        pscmWarehouse: '/pscm/warehouse/info/dialog/v2/page-dialog',
        onlineUsers: '/user/dialog/path/page',
        escOrg: '/esc/org/dialog/page',
        bankRate: '/bank/rate/dialog/v2/page'
      },
      curPage: 1,
      flagInit: true,
      flagOptions: false
    };
  },
  computed: {
    isPlaceholder() {
      return this.placeholder || this.$t('grid.others.pleaseEnterTheSearch');
    },
    optionConfig() {
      Object.assign(this.defaultOption, this.option);
      // console.log('defaultOption--', this.defaultOption)
      return this.defaultOption;
    },
    optionsComputed() {
      // console.log('拿到的选项列表--', this.options)
      return this.options;
    },
    reserveKeyword() {
      return this.multiple;
    },
    // 总页数
    totalPage() {
      if (this.pagination.total === 0) return 0;
      return Math.ceil(this.pagination.total / this.pagination.pageSize);
    },
    pd40() {
      if (this.totalPage > 1 && this.loadingText !== '无数据') return 'pd40';
      return '';
    }
  },
  created() {
    // 返回当前组件指向
    this.mark && this.$emit('getComponent', this);
    // 设置独有id--后面做滚动监听用
    this.markId = 'select' + new Date().getTime() + Math.ceil(Math.random() * 10000);
    this.getOptions(this.value);
  },
  mounted() {
    this.$nextTick(() => {
      this.autoFocus && this.focus();
    });
  },
  // 销毁前触发失焦事件--解决ag-grid编辑状态回车计算问题
  beforeDestroy() {
    const pseudoEvent = {
      mark: 'pseudoEvent',
      target: {
        value: this.value
      }
    };
    this.blurAction(pseudoEvent);
  },
  methods: {
    // 设置值方法
    setValue(value) {
      // console.log('value--', value)
      // eslint-disable-next-line vue/no-mutating-props
      this.value = value;
      this.change(this.value);
    },
    setUrl(params) {
      let lastUrl;
      if (this.defaultUrl) {
        lastUrl = `${this.defaultUrl}`;
      } else if (params.localId) {
        lastUrl = this.type === 'company' || this.type === 'depart' ? `${this.url['company_depart']}` : `${this.url['staff_cost']}`;
      } else if (params.orgid) {
        lastUrl = `${this.url['staff_cost']}/${params.orgid}`;
        delete params.orgid;
      } else {
        lastUrl = `${this.url[this.type]}`;
      }
      lastUrl = this.cancelPage ? lastUrl : `${lastUrl}/${this.pagination.pageNo}/${this.pagination.pageSize}`;
      // console.log('lastUrl', lastUrl)
      return lastUrl;
    },
    // 选择器聚焦方法
    focus() {
      // console.log('this.$refs.agSearchSelect--', this.$refs.agSearchSelect)
      this.$refs.agSearchSelect.$el.children[0].children[0].focus();
    },
    // 下拉框出现/隐藏时触发
    visibleChange(v) {
      // console.log('滚动--', v)
      // console.log('值--', this.value)
      this.flagOptions = v;
      if (v && this.value) {
        this.getOptions(this.value, true, true);
      }
      // if (v) {
      //   const scrollbar = document.getElementsByClassName(this.markId)[0].getElementsByClassName('el-scrollbar')
      //   // el-scrollbar
      //   console.log('scrollbar--', scrollbar)
      //   scrollbar[0].getElementsByClassName('el-scrollbar__wrap')[0].addEventListener('scroll', (e) => {
      //     console.log('滚动事件--', e)
      //   })
      // }
    },

    // 跳转分页
    jumpPage() {
      this.pagination.pageNo = this.hasZeroPagination ? this.curPage - 1 : this.curPage;
      this.getOptions(this.remoteValue, false, false);
    },
    // 切换分页数据
    pageChange(obj) {
      this.pagination.pageNo = obj.page;
      this.pagination.pageSize = obj.limit;
      this.getOptions(this.remoteValue, false, false);
    },
    // 聚焦事件
    focusAction(e) {
      !this.multiple && this.getOptions(this.value, true, false);
      this.$emit('focus', e);
    },
    // 失焦事件触发
    blurAction(e) {
      this.$emit('blur', e);
    },
    // 选项change事件
    change(v) {
      console.log('已选值1--', v);
      let curOption = '';
      if (this.multiple) {
        const obj = {};
        v.forEach(item => {
          obj[item] = true;
        });
        curOption = this.options.filter(item => {
          const key = this.optionConfig.value ? this.optionConfig.value : this.optionConfig.label;
          if (obj[item[key]]) return obj[item[key]];
          return obj[item[this.optionConfig.seledLabel]];
        });
      } else {
        curOption = this.options.find(item => {
          const key = this.optionConfig.value ? this.optionConfig.value : this.optionConfig.label;
          if (item[key]) return item[key] === v;
          return item[this.optionConfig.seledLabel] === v;
        });
      }
      console.log('curOption--', curOption);
      this.$emit('getVal', v); // （ag-grid中使用必须有）
      this.$emit('getOption', curOption); // （返回整个对象，部分场景需要其他值）
      this.$emit('change', curOption);
    },
    // 获取选项列表
    getOptions(value, loading = true, resetPageNo = true) {
      this.loading = loading;
      resetPageNo && (this.pagination.pageNo = 0);
      this.remoteValue = value;
      setTimeout(() => {
        const params = {};
        params[this.searchKey] = Array.isArray(value) ? '' : value;
        if (this.type === 'customer' && this.customerType) {
          params.supParam = this.customerType;
        } else if (this.type === 'artno') {
          this.artnoType && (params.goodsDevCategory = this.artnoType);
          this.categories && (params.sClassification = this.params.data[this.categories]);
        } else {
          params.supParam = '';
        }
        Object.assign(params, this.otherOptions);
        const nextParams = typeof this.transformSearchParams === 'function' ? this.transformSearchParams(params) : params;
        const lastUrl = this.setUrl(nextParams);
        if (this.method === 'post') {
          request({
            url: lastUrl,
            method: 'POST',
            data: nextParams,
            noLoading: true
          }).then(res => {
            const tempOptions = [];
            res.data.content.forEach(item => {
              if (item) {
                tempOptions.push(item);
              }
            });
            this.options = tempOptions;
            if (this.options.length === 0 && !value) {
              this.loading = false;
            } else {
              this.loading = false;
            }
            this.pagination.total = res.data.totalElements;
            this.curPage = this.hasZeroPagination ? this.pagination.pageNo + 1 : this.pagination.pageNo;
            this.type === 'currency' && this.defaultCurrency(this.options);
            this.labelDefault && this.defaultCurrency(this.options);
            if (this.flagOptions) {
              // 触发resize事件
              setTimeout(() => {
                const resizeE = new Event('resize');
                window.dispatchEvent(resizeE);
              }, 10);
            }
          }).catch(error => {
            console.log('请求失败--', error);
          }).finally(() => {
            // this.loading = false
          });
        } else {
          request({
            url: lastUrl,
            method: 'GET',
            params,
            noLoading: true
          }).then(res => {
            console.log('res', res);
            const tempOptions = [];
            const resData = res.data || {};
            const data = resData.content ? resData.content : resData.records;
            data.forEach(item => {
              if (item) {
                tempOptions.push(item);
              }
            });
            this.options = tempOptions;
            this.pagination.total = resData.totalElements ? resData.totalElements : resData.total;
            this.curPage = this.hasZeroPagination ? this.pagination.pageNo + 1 : this.pagination.pageNo;
            this.type === 'currency' && this.defaultCurrency(this.options);
            this.labelDefault && this.defaultCurrency(this.options);
            if (this.flagOptions) {
              // 触发resize事件
              setTimeout(() => {
                const resizeE = new Event('resize');
                window.dispatchEvent(resizeE);
              }, 10);
            }
          }).catch(error => {
            console.log('请求失败--', error);
          }).finally(() => {
            this.loading = false;
          });
        }
      }, 200);
    },
    // 币种--币种时给默认人民币
    defaultCurrency(options) {
      if (this.value) return;
      this.flagInit = false;
      options.forEach(item => {
        if (item.sPath === this.currency) {
          // eslint-disable-next-line vue/no-mutating-props
          this.value = item.sPath;
        }
      });
      this.change(this.value);
    }
  }
};
