import { defineStore, pinia } from 'pinia';

// 创建 Pinia Store
const cubeCacheStore = defineStore({
  id: 'cubeCache',
  state: () => ({
    cacheData: {},
  }),
  getters: {
    getAllCache: (state) => state.cacheData,
    getCacheByKey: (state) => (key) => state.cacheData[key],
  },
  actions: {
    getCacheKey() {
      // 生成唯一缓存 key
      return Date.now().toString();
    },
    setCache(cacheKey, cacheInfo, time = 0) {
      // 设置缓存
      if (!this.cacheData[cacheKey]) {
        this.cacheData[cacheKey] = {};
      }
      const version = Object.keys(this.cacheData[cacheKey]).length;
      this.cacheData[cacheKey][`version${version}`] = cacheInfo;
      if (time > 0) {
        // 自动保存间隔
        const saveInterval = setInterval(() => {
          const latestVersion = this.cacheData[cacheKey][`version${version}`];
          if (!latestVersion || JSON.stringify(latestVersion) !== JSON.stringify(cacheInfo)) {
            this.setCache(cacheKey, cacheInfo, 0);
          }
        }, time);
      }
    },
    cancelAutoSave(cacheKey) {
      // 取消时间自动缓存
      // 可以通过清除 setInterval 来取消自动保存
    },
    clearCache(cacheKey) {
      // 清空指定缓存
      if (this.cacheData[cacheKey]) {
        delete this.cacheData[cacheKey];
        return true;
      }
      return false;
    },
    clearAllCache() {
      // 清空所有缓存
      this.cacheData = {};
    },
  },
});
