import "core-js/modules/es.array.push.js";
import localPagination from '../../Pagination/index.js';
import { request } from '@cndinfo/cube-web-utils';
import '../../../../lib/styles/components/SearchSelect.scss';
export default {
  name: 'SearchSelect',
  components: {
    localPagination
  },
  props: {
    // 接口地址
    defaultUrl: {
      type: String,
      default: ''
    },
    // 检索类型
    type: {
      type: String,
      default: 'staff'
    },
    // 客商类型字段
    customerType: {
      type: String,
      default: ''
    },
    // 货号类型字段
    artnoType: {
      type: String,
      default: ''
    },
    // 商品分类
    categories: {
      type: String,
      default: ''
    },
    // 检索字段
    searchKey: {
      type: String,
      default: 'sName'
    },
    value: {
      type: [String, Array],
      default: ''
    },
    multipleLimit: {
      type: Number,
      default: 0
    },
    filterMethod: {
      type: Function,
      default: null
    },
    disabled: {
      type: Boolean,
      default: false
    },
    multiple: {
      type: Boolean,
      default: false
    },
    clearable: {
      type: Boolean,
      default: true
    },
    label: {
      type: String,
      default: ''
    },
    placeholder: {
      type: String,
      default: ''
    },
    size: {
      type: String,
      default: ''
    },
    option: {
      type: Object,
      default: () => {
        return {};
      }
    },
    otherOptions: {
      type: Object,
      default: () => {}
    },
    defaulth: {
      type: Boolean,
      default: false
    },
    method: {
      type: String,
      default: 'post'
    },
    currency: {
      type: String,
      default: '人民币'
    },
    labelDefault: {
      type: String,
      default: ''
    },
    content: {
      type: String,
      default: 'content'
    },
    cancelPage: {
      type: Boolean,
      default: false
    },
    // 初次调用接口
    firstCall: {
      type: Boolean,
      default: false
    },
    totalElements: {
      type: String,
      default: 'totalElements'
    }
  },
  data() {
    return {
      loadingText: '加载中',
      defaultOption: {
        selectLabel: 'sName',
        // 输入框展示
        label: 'sPath',
        // 下拉框展示
        value: this.type === 'applicant' ? 'account' : 'sName',
        // 远程搜索值
        valueKey: 'sId' // 取值
      },

      markId: '',
      pagination: {
        total: 0,
        pageNo: 0,
        pageSize: 20
      },
      loading: false,
      visitedOptions: [],
      // 所有选项值
      options: [],
      // 当前选项列表
      url: {
        byLevel: '/security/org/availables/by-level',
        roleAvailables: '/security/role/availables',
        // otter
        company: '/org/dialog/corp/v2/page',
        depart: '/org/dialog/busi/v2/page',
        cost: '/org/dialog/cost/v2/page',
        area: '/area/dialog/v2/page',
        staff: '/staff/dialog/org/v2/page',
        customer: '/customer/dialog/v2/page',
        customerRock: '/customer/dialog/v3/page/rock.basekit.com.supplier',
        currency: '/currency/v2/page',
        artno: '/dialog/artno/v2/page',
        measurementunit: '/measurementunit/dialog/v2/query',
        warehouse: '/warehouse/info/dialog/v2/page-dialog',
        creater: '/user/dialog/path/page',
        bank: '/bank/dialog/v2/page',
        tax: '/tax/type/page',
        goods: '/goods/dialog/detail/query/v2',
        applicant: '/user/dialog/path/page',
        // root: '/org/bdaorguserrelation/get/by/current/user',
        // portcity: '/portcity/dialog/name/like',
        portcustoms: '/portcustoms/dialog/name/like',
        groupcustomers: '/bda/groupCustomer/dialog/v2/page',
        bookingOrder: '/stock/booking/dialog/v2/page',
        // cargoContainer: '/comm/container/dialog/v2/page',
        company_depart: '/org/dialog/corporbus/page',
        staff_cost: '/staff/info/byorgid',
        sModifier: '/paper/homepage/notice/system/page',
        role: '/role/dialog/page',
        tenant: '/org/dialog/root/page',
        menu: '/sys/menu',
        bankCategory: '/bankCategory/page',
        hscode: '/hscode/bdahscode/dialog/page-dialog',
        root: '/org/root/page',
        paperCustomer: 'paper/customer/dialog/v2/page',
        pscmGoodsDetail: 'pscm/goods/detail/query/v2/page',
        pscmGoodsAttribute: 'pscm/goods/attribute/query/v2/page',
        paperStock: 'paper/stock/balance/summary/sum/query',
        cargoContainer: 'comm/container/dialog/v2/page',
        staffDialog: '/staff/dialog/org/v3/page',
        // portcity: '/portcity/dialog/name/like',
        orgRoot: '/org/root/page',
        portcity: '/portcity/page',
        interaction: '/interaction/payconfig/page',
        factor: '/sys/datatableresource/dialog/page',
        affectField: '/sys/datafieldresource/dialog/page',
        QC: '/configure/liorgrelation/role-user/page',
        transfer: '/configure/liorgrelation/role-user/page',
        customerLevel: '/bda/user/customer/level/dialog/page',
        taxType: '/tax/type/dialog/v2/page',
        taxationtype: '/invoice/sale/tax-detail/taxationtype/page',
        pscmWarehouse: '/pscm/warehouse/info/dialog/v2/page-dialog',
        onlineUsers: '/user/dialog/path/page',
        escOrg: '/esc/org/dialog/page',
        bankRate: '/bank/rate/dialog/v2/page'
      },
      curPage: 1,
      flagInit: true,
      flagOptions: false,
      curOption: null,
      tempOptions: null // 存储已选值
    };
  },

  computed: {
    isPlaceholder() {
      return this.$t('components.pleaseSelect');
      // return this.placeholder ? this.placeholder : this.label ? Cookies.get('language') === 'en' ? this.$t('components.pleaseSelect') + ' ' + this.label : this.$t('components.pleaseSelect') + this.label : this.$t('grid.others.pleaseEnterTheSearch')
    },

    optionConfig() {
      Object.assign(this.defaultOption, this.option);
      // console.log('defaultOption--', this.defaultOption)
      return this.defaultOption;
    },
    optionsComputed() {
      // console.log('拿到的选项列表--', this.options)
      return this.options;
    },
    reserveKeyword() {
      return this.multiple;
    },
    // 总页数
    totalPage() {
      if (this.pagination.total === 0) return 0;
      return Math.ceil(this.pagination.total / this.pagination.pageSize);
    },
    pd40() {
      if (this.totalPage > 1 && this.loadingText !== '无数据') return 'pd40';
      return '';
    },
    defaultH() {
      return this.defaulth ? '' : 'defaultH';
    },
    selectValue: {
      get() {
        return this.value;
      },
      set(val) {
        this.$emit('update:value', val);
      }
    }
  },
  watch: {
    curPage(nv, ov) {
      // console.log('当前页--', nv)
      this.pagination.pageNo = nv - 1;
    },
    'pagination.pageNo'(nv, ov) {
      // console.log('分页配置--', nv)
      this.curPage = nv + 1;
    }
  },
  created() {
    // 设置独有id
    this.markId = 'select' + new Date().getTime() + Math.ceil(Math.random() * 10000);
    if (this.multiple) {
      if (this.labelDefault || this.firstCall) {
        this.getAllOptions();
        this.value && this.change(this.value);
      }
    } else {
      if (this.type === 'currency' || this.labelDefault || this.firstCall) {
        this.getOptions(this.value);
      }
    }
  },
  methods: {
    inputAction(val) {
      if (val) {
        val = Number(val);
        if (this.curPage > this.totalPage) {
          return this.curPage = this.totalPage;
        } else if (val === 0) {
          this.curPage = 1;
        }
      }
    },
    // 移除tag时触发
    removeTag(tag) {
      const key = this.optionConfig.valueKey ? this.optionConfig.valueKey : this.optionConfig.value ? this.optionConfig.value : this.optionConfig.label;
      console.log('tag--', key, tag);
      // const key = this.optionConfig.value ? this.optionConfig.value : this.optionConfig.label
      this.tempOptions = this.tempOptions.filter(item => {
        console.log('removeTag', item);
        return item[key] !== tag;
      });
      this.change(this.value);
    },
    // 下拉框出现/隐藏时触发
    visibleChange(v) {
      // console.log('滚动--', v)
      // console.log('值--', this.value)
      this.flagOptions = v;
      if (v && this.value) {
        this.getOptions(this.value, true, true);
      }
      // if (v) {
      //   const scrollbar = document.getElementsByClassName(this.markId)[0].getElementsByClassName('el-scrollbar')
      //   // el-scrollbar
      //   console.log('scrollbar--', scrollbar)
      //   scrollbar[0].getElementsByClassName('el-scrollbar__wrap')[0].addEventListener('scroll', (e) => {
      //     console.log('滚动事件--', e)
      //   })
      // }
    },

    // 跳转分页
    jumpPage() {
      this.tempOptions = this.curOption;
      this.getOptions(this.value, false, false);
    },
    // 切换分页数据
    pageChange(obj) {
      this.tempOptions = this.curOption;
      this.pagination.pageNo = obj.page;
      this.pagination.pageSize = obj.limit;
      this.getOptions(this.value, false, false);
    },
    setUrl(params) {
      let lastUrl;
      if (this.defaultUrl) {
        lastUrl = `${this.defaultUrl}`;
      } else if (params.localId) {
        lastUrl = this.type === 'company' || this.type === 'depart' ? `${this.url['company_depart']}` : `${this.url['staff_cost']}`;
      } else if (params.orgid) {
        lastUrl = `${this.url['staff_cost']}/${params.orgid}`;
        delete params.orgid;
      } else {
        lastUrl = `${this.url[this.type]}`;
      }
      lastUrl = this.method === 'post' ? this.cancelPage ? lastUrl : `${lastUrl}/${this.pagination.pageNo}/${this.pagination.pageSize}` : `${this.url[this.type]}`;
      return lastUrl;
    },
    // 聚焦事件
    focus(e) {
      !this.multiple && this.getOptions(this.value, true, false);
      this.$emit('focus', e);
    },
    // 选项change事件
    change(v) {
      console.log('已选值1--', v);
      this.curOption = '';
      if (this.multiple) {
        const key = this.optionConfig.valueKey ? this.optionConfig.valueKey : this.optionConfig.value ? this.optionConfig.value : this.optionConfig.label;
        const obj = {};
        v.forEach((item, index) => {
          obj[item[key]] = true;
        });
        this.curOption = this.visitedOptions.filter(item => {
          if (obj[item[key]]) return obj[item[key]];
          return obj[item[this.optionConfig.selectLabel]];
        });
        console.log('this.curOption', this.curOption);
        // if (this.tempOptions && this.tempOptions.length > 0) {
        //   this.curOption = this.uniqueConcat(this.curOption, this.tempOptions)
        // }
        this.tempOptions = this.curOption;
      } else {
        console.log('this.options', this.options);
        const key = this.optionConfig.valueKey ? this.optionConfig.valueKey : this.optionConfig.value ? this.optionConfig.value : this.optionConfig.label;
        this.curOption = this.options.find(item => {
          // const key = this.optionConfig.value ? this.optionConfig.value : this.optionConfig.label
          // if (item[key]) return item[key] === v
          // return item[this.optionConfig.selectLabel] === v
          if (item[key]) return item[key] === v[key];
          return item[this.optionConfig.selectLabel] === v[this.optionConfig.selectLabel];
        });
      }
      console.log('curOption--', this.curOption);
      this.$emit('input', v);
      this.$emit('change', this.curOption);
    },
    // 获取所以选项
    getAllOptions() {
      const params = {};
      if ((this.type === 'customer' || this.type === 'paperCustomer') && this.customerType) {
        params.supParam = this.customerType;
      } else if (this.type === 'artno') {
        this.artnoType && (params.goodsDevCategory = this.artnoType);
        this.categories && (params.sClassification = this.categories);
      } else {
        params.supParam = '';
      }
      Object.assign(params, this.otherOptions);
      // 拼接pageNo、pageSize
      const lastUrl = this.defaultUrl ? `${this.defaultUrl}/0/999999` : `${this.url[this.type]}/0/999999`;
      request({
        url: lastUrl,
        method: 'POST',
        data: params,
        noLoading: true
      }).then(res => {
        const tempOptions = [];
        const data = res.data[this.content];
        const forFunc = item => {
          if (item) {
            tempOptions.push(item);
          }
        };
        data.forEach(forFunc);
        this.visitedOptions = tempOptions;
        this.options = this.visitedOptions;
        // this.options = this.visitedOptions.slice(0, 19)
        this.pagination.total = data[this.totalElements];
      }).catch(error => {
        console.log('请求失败--', error);
      }).finally(() => {});
    },
    // 获取选项列表
    getOptions(value, loading = true, resetPageNo = true) {
      const key = this.optionConfig.value ? this.optionConfig.value : this.optionConfig.label;
      if (this.multiple) {
        // value = value.map(node => node.sName).toString()
      } else {
        value = value[key] ? value[key] : value;
      }
      this.loading = loading;
      resetPageNo && (this.pagination.pageNo = 0);
      setTimeout(() => {
        const params = {};
        params[this.searchKey] = Array.isArray(value) ? '' : value;
        if ((this.type === 'customer' || this.type === 'paperCustomer') && this.customerType) {
          params.supParam = this.customerType;
        } else if (this.type === 'artno') {
          this.artnoType && (params.goodsDevCategory = this.artnoType);
          this.categories && (params.sClassification = this.categories);
        } else {
          params.supParam = '';
        }
        Object.assign(params, this.otherOptions);
        if (params.localId && this.type === 'company') {
          params.orgType = 200;
        } else if (params.localId && this.type === 'depart') {
          params.orgType = 100;
        }
        const lastUrl = this.setUrl(params);
        // 拼接pageNo、pageSize
        // const lastUrl = `${this.url[this.type]}/${this.pagination.pageNo}/${this.pagination.pageSize}`
        if (this.method === 'post') {
          request({
            url: lastUrl,
            method: 'POST',
            data: params,
            noLoading: true
          }).then(res => {
            const data = res.data?.[this.content] || [];
            const tempOptions = [];
            const forFunc = item => {
              if (item) {
                tempOptions.push(item);
              }
            };
            data.forEach(forFunc);
            this.options = tempOptions;
            if (data.length === 0) {
              this.loadingText = '无数据';
            } else {
              this.loading = false;
              this.pagination.total = data[this.totalElements];
              this.type === 'currency' && !!this.currency && this.defaultCurrency(this.options);
              this.labelDefault && this.defaultCurrency(this.options);
            }
            if (this.flagOptions) {
              // 触发resize事件
              setTimeout(() => {
                const resizeE = new Event('resize');
                window.dispatchEvent(resizeE);
              }, 10);
            }
          }).catch(error => {
            console.log('请求失败--', error);
          }).finally(() => {});
        } else {
          request({
            url: lastUrl,
            method: 'GET',
            data: params,
            noLoading: true
          }).then(res => {
            const tempOptions = [];
            const data = this.content ? res.data[this.content] : res.data;
            data.forEach(item => {
              if (item) {
                tempOptions.push(item);
              }
            });
            this.options = tempOptions;
            if (this.options.length === 0 && !value) {
              this.loadingText = '无数据';
            } else {
              this.loading = false;
            }
            this.pagination.total = res.data[this.totalElements];
            this.type === 'currency' && !!this.currency && this.defaultCurrency(this.options);
            this.labelDefault && this.defaultCurrency(this.options);
            if (this.flagOptions) {
              // 触发resize事件
              setTimeout(() => {
                const resizeE = new Event('resize');
                window.dispatchEvent(resizeE);
              }, 10);
            }
          }).catch(error => {
            console.log('请求失败--', error);
          }).finally(() => {
            this.loading = false;
          });
        }
      }, 200);
    },
    // 币种--币种时给默认人民币
    defaultCurrency(options) {
      if (this.value && !this.labelDefault) return;
      options.forEach(item => {
        if (item.sPath === this.currency) {
          this.selectValue = item.sPath;
        }
      });
      this.change(this.value);
    },
    // 去重合并
    uniqueConcat(arr1, arr2) {
      const arr = arr1.concat(arr2);
      const keyObj = {};
      const resArr = [];
      arr.forEach(item => {
        if (!keyObj[item[this.optionConfig.valueKey]]) {
          resArr.push(item);
          keyObj[item[this.optionConfig.valueKey]] = 1;
        }
      });
      return resArr;
    }
  }
};
