// 判断html渲染代码a标签跳转是否在项目中跳转
function htmlTarget(str) {
  const reg = /<a[^>]*href=[ '"]([^"]*)[' "][^>]*>(.*?)<\/a>/g
  const a = str.replace(/\<a.*?\/a>/g, function(keyword) {
    if (reg.exec(str) != null) {
      const x = RegExp.$1
      const y = RegExp(window.location.origin)
      if (y.test(x)) {
        keyword = keyword.replace(/_blank/g, '_self')
      }
    }
    return `${keyword}`
  })
  return a
}
export default {
  htmlTarget
}
