    spreadColumnDef,
    ...middleColumnDefs,
    hasSecMeter ? lastSecDefMeter : lastDefMeter
  ].filter((item) => item)

  const layoutList = []
  totalItem.forEach((item) => {
    if (
      item.headerName &&
      item.headerName !== '' &&
      item.headerName !== 'checkbox' &&
      item.field
    ) {
      layoutList.push({ field: item.field })
    }
  })

  return totalItem.map((item) => {
    if (!item.columnsMenuParams) {
      item.columnsMenuParams = {}
