          : hasSecMeter
            ? childSecMeter
            : childMeter
      )
    }
  }
  if (childColumnDefs[childColumnDefs.length - 1].field !== 'fill') {
    childColumnDefs.push({
      headerName: '',
      field: 'fill',
      minWidth: 0,
      flex: 1,
      suppressColumnsToolPanel: true
    })
  }
  return childColumnDefs.map((item) => {
    if (hasSecMeter) {
