/**
 * 获取当前的运行环境
 * @returns string: app、web、mp-weixin
 */
export function getCurrentEvn() {
  let platform
  try {
    platform = uni.getSystemInfoSync().uniPlatform
  } catch (e) {
    platform = "web"
  }
  return platform
}

/**
 * 判断当前环境是否是iOS
 * @returns boolean
 */
export function checkIsIOS() {
  try {
    let isIOS = false
    const uniPlatform =
      uni &&
      uni.getSystemInfoSync() &&
      uni.getSystemInfoSync().platform === "ios"
    const plusOsName = plus && plus.os && plus.os.name === "iOS"
    const plusAgent =
      plus && navigator && plus.navigator.getUserAgent().match(/(iphone os)/i)
    const windowsAgent =
      window &&
      !!window.navigator.userAgent.match(/\(i[^;]+;( U;)? CPU.+Mac OS X/)

    if (uniPlatform || plusOsName || plusAgent || windowsAgent) {
      isIOS = true
    }
    return isIOS
  } catch (e) {
    return false
  }
}
